mapper.select:
select 

from t_upload_info
where user_name = #{userName,jdbcType=VARCHAR}
  and year = #{year,jdbcType=VARCHAR}

mapper.delete:
delete from t_upload_info
where user_name = #{userName,jdbcType=VARCHAR}
  and year = #{year,jdbcType=VARCHAR}

mapper.update:
update t_upload_info


    table_id = #{tableId,jdbcType=INTEGER},


    uploaded = #{uploaded,jdbcType=INTEGER},


where user_name = #{userName,jdbcType=VARCHAR}
  and year = #{year,jdbcType=VARCHAR}

update t_upload_info
set table_id = #{tableId,jdbcType=INTEGER},
  uploaded = #{uploaded,jdbcType=INTEGER}
where user_name = #{userName,jdbcType=VARCHAR}
  and year = #{year,jdbcType=VARCHAR}
